        binding.listview.adapter = userAdapter;

        binding.searchView.setOnQueryTextListener(object:SearchView.OnQueryTextListener,
            androidx.appcompat.widget.SearchView.OnQueryTextListener {
            override fun onQueryTextSubmit(query: String?): Boolean {
                binding.searchView.clearFocus()
                if (user.contains(query)) {

                    userAdapter.filter.filter(query)

                }
                return false
            }

            override fun onQueryTextChange(newText: String?): Boolean {
                userAdapter.filter.filter(newText)
                return false
            }
        })

        viewphoto=findViewById(R.id.viewPhoto)
        dots1=findViewById(R.id.dots1)
        dots2=findViewById(R.id.dots2)
        dots3=findViewById(R.id.dots3)

        val imglist = listOf(R.drawable.img1,R.drawable.img2,R.drawable.img3)
        val adepter=ViewAdepter(imglist)
        viewphoto.adapter=adepter

        viewphoto.registerOnPageChangeCallback(object :ViewPager2.OnPageChangeCallback(){
            override fun onPageScrolled(
                position: Int,
                positionOffset: Float,
                positionOffsetPixels: Int
            ) {
                changColor()
                super.onPageScrolled(position, positionOffset, positionOffsetPixels)
            }

            override fun onPageSelected(position: Int) {
                super.onPageSelected(position)
            }

            override fun onPageScrollStateChanged(state: Int) {
                super.onPageScrollStateChanged(state)
                changColor()
            }

        })
    }
    fun changColor(){
        when(viewphoto.currentItem){
            0->{
                dots1.setBackgroundColor(applicationContext.resources.getColor(R.color.black))
                dots2.setBackgroundColor(applicationContext.resources.getColor(R.color.teal_200))
                dots3.setBackgroundColor(applicationContext.resources.getColor(R.color.teal_200))
            }
            1->{
                dots1.setBackgroundColor(applicationContext.resources.getColor(R.color.teal_200))
                dots2.setBackgroundColor(applicationContext.resources.getColor(R.color.black))
                dots3.setBackgroundColor(applicationContext.resources.getColor(R.color.teal_200))
            }
            2->{
                dots1.setBackgroundColor(applicationContext.resources.getColor(R.color.teal_200))
                dots2.setBackgroundColor(applicationContext.resources.getColor(R.color.teal_200))
                dots3.setBackgroundColor(applicationContext.resources.getColor(R.color.black))
            }
        }
    }
